import { useRouter } from "./useRouter";

export const useFilterType = ({pathname, search}) => {
  const getQueryString = new URLSearchParams(decodeURIComponent(search).replace(/,/g, '+'));
  const queryStringValue = getQueryString.get('type')
  const {routeTo} = useRouter()

  // fn2 라우팅 기능, searchparams는 return을 최종 string 형태로 하게됨.
  const handleTypeQueryString = (type:string) => {
    // console.log("getQueryString",getQueryString)
    // const pathResult = `${pa}`
    getQueryString.set('type', type)
    return routeTo(getQueryString.toString())
  }
  // fn2 함수를 호출할때 routerTo(fn2(key 등 인자 값))

  return {
    getQueryStringValue: queryStringValue?.split(" "),
    handleTypeQueryString
  }
}
