import { useState } from 'react'
import { Fragment } from 'react'
import { useRouter } from '../../useHook/useRouter'

import styles from './SideBarItem.module.scss'

interface ISideBarItem {
  id: number,
  path: string,
  label: string
}


export const SideBarItem = () => {
 const{ routeTo, currentPath } = useRouter()
 const [isClickItem, setIsClickItem] = useState(false)

  const sideItems: ISideBarItem[] = [
    {
      id: 0,
      path: '/beers',
      label: '맥주'
    },
    {
      id: 1,
      path: '/wines',
      label: '와인'
    },
    {
      id: 2,
      path: '/coffe',
      label: '커피'
    },
  ]

  const onClickItem = (path: string) => {
    if(path === '/beverage') {
      setIsClickItem(true)
    } 
    routeTo(path)
  }
  console.log(isClickItem)
  return (
    <>
      {
        sideItems.map(item =>
          <Fragment key={item.path}>
            <ul 
              className={isClickItem ? styles.selectedWrapper: styles.itemWrapper}
            >
              <li
                className={`${styles.item} ${currentPath === item.path ? styles.selected : ''}`} 
                onClick={()=> onClickItem(item.path)}
              > {item.label} </li>
            </ul>
          </Fragment>
          )
      }
    </>
  )
}
