import { useCallback, useEffect, useRef, useState } from "react"
import { useLocation } from "react-router-dom"
import { getWine } from "../../api/beverage"
import { IWine, IBeverageType } from "../../types/beverage"
import { useFilterType } from "../../useHook/useFilterType"
import useInfiniteScroll from "../../useHook/useInfiniteScroll"
import { ListItem } from "./ListItem"
// import {FaWineGlassAlt} from 'react-icons/fa'
import { TypeList } from "./TypeList"

import styles from './WineList.module.scss'

export const wineTypes: IBeverageType[] = [
  {
    id: 0,
    label: '레드 와인',
    src: '/img/red-wine.jpg',
    type: 'reds',
    alt: 'red wine'
  },
  {
    id: 1,
    label: '화이트 와인',
    src: '/img/white-wine.jpg',
    type: 'whites',
    alt: 'white wine'
  },
  {
    id: 2,
    label: '로제 와인',
    src: '/img/rose-wine.jpg',
    type: 'rose',
    alt: 'rose wine'
  },
  {
    id: 3,
    label: '스파클링 와인',
    src: '/img/sparkling-wine.jpg',
    type: 'sparkling',
    alt: 'sparkling wine'
  },
  {
    id: 4,
    label: '포트 와인',
    src: '/img/port-wine.jpg',
    type: 'port',
    alt: 'port wine'
  },
  {
    id: 5,
    label: '디저트',
    src: '/img/dessert.jpg',
    type: 'dessert',
    alt: 'dessert'
  },
]

type type = "single" | "multi"

export const WineList = () => {
  const [data, setData] = useState<IWine[] | []>([])
  const {search} = useLocation()
  const {getQueryStringValue} = useFilterType({search})
  const fetchMoreEl = useRef<HTMLDivElement | null>(null)
  const isFetchedData = useRef<boolean>(false)
  const intersecting = useInfiniteScroll(fetchMoreEl)

  const fetchData = useCallback(async (type: type, mutate?: string|null)=>{
    if(type === "single") {
      const getWineData = await getWine(
        getQueryStringValue || [], 
        mutate ? {from:data.length,size:data.length+20} : null,
        "single"
        )
      setData(prev => [...prev, ...getWineData])
    } 
    if(type === "multi") {
      if(mutate) {
        console.log("multi_multi")
        const getWineData = await getWine(
          getQueryStringValue,
          {from:data.length,size:data.length+20},
          "multi"
        )
        setData(prev => [...prev, ...getWineData])
      } else {
        console.log("multi_first")
        const getWineData = await getWine(
          getQueryStringValue,
          null,
          "multi"
          )
        setData(getWineData)
      }
    }
    isFetchedData.current = true
  },[data, search])

    useEffect(()=>{ 
      // 초기 기본 데이터 페칭
      if(!isFetchedData.current) fetchData("single")
      console.log("getQueryStringValue",getQueryStringValue)
    }, [])

    useEffect(()=>{
      // 무한 스크롤을 위한 mutate
      if(isFetchedData.current && intersecting) fetchData("single", "mutate")
      if(getQueryStringValue?.length > 0) {
        if(isFetchedData.current && intersecting) {
          console.log("isFetchedData.current12121121",isFetchedData.current)
          fetchData("multi", "mutate")
        } else if(!isFetchedData.current && !intersecting){
          console.log("isFetchedData.current",isFetchedData.current)
          fetchData("multi")
        }
      }

    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[intersecting, search])

  return (   
      <div>
        <TypeList title={"와인"} typeList={wineTypes}/>
        <h1 className={styles.header}> 레드와인을 알아봐요 </h1>
        <section className={styles.itemWrapper}>
          {
            data?.length > 0
            ? data.map((el:IWine) => {
              return (
                  <ListItem 
                    key={el.image+el.id}
                    src={el.image}
                    title={el.wine} 
                    info={el.winery} 
                    options={el.rating} 
                  /> 
              )
            })
            : <div>loading...</div>
          }
        </section>
        <div ref={fetchMoreEl} style={{ height: "200px" }} />
      </div>
  )
}
